sqlMap.select:
/* ID : ipInfo.getIpInfoList */ select RTRIM(CAST(sys_group_id AS CHAR(10))) as sys_group_id, RTRIM(CAST(system_id AS CHAR(10))) as system_id, system_ip , ip_desc, ins_id, DATE_FORMAT(ins_date, '%Y-%m-%d %H:%i:%s') as ins_date, upt_id , DATE_FORMAT(upt_date, '%Y-%m-%d %H:%i:%s') as upt_date , domain_addr, mfu_ip , system_name, sys_group_name, ip_use_class, ip_use_code, ip_use_name, ip_use_item from( select * from ( select Row_Number() over (order by sys_group_id desc, system_id desc, system_ip desc) RowNum2, TB_IN.* from ( select Row_Number() over (order by a.sys_group_id, a.system_id, system_ip) RowNum1 , a.sys_group_id, a.system_id, system_ip, ip_desc, a.ins_id, a.ins_date , a.upt_id, a.upt_date, b.system_name, c.sys_group_name , a.ip_use_class, a.ip_use_code, a.domain_addr , d.code_name ip_use_name, a.ip_use_item , a.mfu_ip FROM tb_sys_ipinfo a join tb_sys_sysinfo b on a.sys_group_id = b.sys_group_id and a.system_id = b.system_id join tb_sys_sysgroup c on a.sys_group_id = c.sys_group_id left outer join tb_mng_basecode d on d.class_id = 'ip_use' and d.code_id = a.ip_use_code
b.system_name a.system_ip Like CONCAT('%', #findText#, '%')
a.system_id = #systemId#
a.ip_use_item like CONCAT('%', #ipUseItem#, '%')
a.sys_group_id = #findSysGroupId#
) TB_IN WHERE RowNum1 <= #iEPageNo# ) TB_OUT WHERE RowNum2 <= #iSPageNo# ) tbl2 order by sys_group_id, system_id, system_ip

sqlMap.insert:
/* ID : ipInfo.setNatRangeInsert */
INSERT INTO TB_MNG_NAT_RANGE(TENANT_ID, RANGE_SEQ, IP_CLASS, NAT_IP_RANGE)

VALUES (#findTenantId#, LPAD(#rangeSeq#, 5, '0'), #ipClass#, #natIpRange#)
