sqlMap.select:
/* ID : ARGOCOMMON.login */ SELECT
CASE WHEN DATEADD(MONTH,CONVERT(int, '-'+ISNULL( (SELECT CASE VAL_CUR WHEN '' THEN '500' WHEN '0' THEN '500' ELSE VAL_CUR END VAL_CUR FROM TB_WAS_CONFIG WHERE SECTION = 'COMPANY' AND KEY_CODE = 'SITE_PWD_CHANGE'), '500')) ,CONVERT(VARCHAR(10), GETDATE(),121)) > CONVERT(DATE,ISNULL(A.USER_PWD_UPT_DATE,'19900101')) THEN 'PWDCHG' ELSE 'LOGINOK' END AS RESULT_CD , A.USER_ID AS AGENT_ID, A.TENANT_ID AS TENANT_ID, A.SALT,A.USER_PWD , CASE WHEN A.PLAYER_KIND = '' THEN '0' WHEN ISNULL(A.PLAYER_KIND,'') = '' THEN '0' ELSE A.PLAYER_KIND END AS PLAYER_KIND , CASE WHEN A.REAL_PLAY_KIND = '' THEN '0' WHEN ISNULL(A.REAL_PLAY_KIND,'') = '' THEN '0' ELSE A.REAL_PLAY_KIND END AS REAL_PLAY_KIND , A.USER_ID, A.GRANT_ID, A.USER_NAME, CONVERT(CHAR(8), GETDATE(), 8) AS LOGIN_TIME, CONVERT(CHAR(8), GETDATE(), 112) LOGIN_DATE , A.CONVERT_FLAG, A.GROUP_ID, (SELECT DEPTH FROM TB_MNG_GROUP WHERE TENANT_ID = #tenantId# AND GROUP_ID = A.GROUP_ID) AS DEPTH , (SELECT GRANT_NAME FROM TB_MNG_AUTH WHERE TENANT_ID = #tenantId# AND GRANT_ID = A.GRANT_ID) AS GRANT_NAME, B.AUTH_RANK , A.FORCED_LOGOUT, ISNULL(A.CONTROL_AUTH,'') CONTROL_AUTH, ISNULL(A.MAIN_PAGE,'') MAIN_PAGE , ISNULL(A.LOGIN_DATE_CHECK_USE,'') LOGIN_DATE_CHECK_USE , ISNULL(A.LOGIN_DATE_CHECK_FROM,'') LOGIN_DATE_CHECK_FROM , ISNULL(A.LOGIN_DATE_CHECK_TO,'') LOGIN_DATE_CHECK_TO , ISNULL(A.LOGIN_ERR_COUNT,0) LOGIN_ERR_COUNT , ISNULL((SELECT VAL_CUR FROM TB_WAS_CONFIG WHERE SECTION = 'COMPANY' AND KEY_CODE = 'SITE_PWD_FAIL_COUNT'), 0) SITE_PWD_FAIL_COUNT FROM TB_MNG_USERINFO A WHERE A.TENANT_ID = #tenantId# AND A.USER_ID = #agentId#
A.USER_PWD = #agentPw#

/* ID : ARGOCOMMON.login */ SELECT
CASE WHEN DATEADD(MONTH,CONVERT(int, '-'+ISNULL( (SELECT CASE VAL_CUR WHEN '' THEN '500' WHEN '0' THEN '500' ELSE VAL_CUR END VAL_CUR FROM TB_WAS_CONFIG WHERE SECTION = 'COMPANY' AND KEY_CODE = 'SITE_PWD_CHANGE'), '500')) ,CONVERT(VARCHAR(10), GETDATE(),121)) > CONVERT(DATE,ISNULL(A.USER_PWD_UPT_DATE,'19900101')) THEN 'PWDCHG' ELSE 'LOGINOK' END AS RESULT_CD , A.USER_ID AS AGENT_ID, A.TENANT_ID AS TENANT_ID, A.SALT,A.USER_PWD , CASE WHEN A.PLAYER_KIND = '' THEN '0' WHEN ISNULL(A.PLAYER_KIND,'') = '' THEN '0' ELSE A.PLAYER_KIND END AS PLAYER_KIND , CASE WHEN A.REAL_PLAY_KIND = '' THEN '0' WHEN ISNULL(A.REAL_PLAY_KIND,'') = '' THEN '0' ELSE A.REAL_PLAY_KIND END AS REAL_PLAY_KIND , A.USER_ID, A.GRANT_ID, A.USER_NAME, CONVERT(CHAR(8), GETDATE(), 8) AS LOGIN_TIME, CONVERT(CHAR(8), GETDATE(), 112) LOGIN_DATE , A.CONVERT_FLAG, A.GROUP_ID, (SELECT DEPTH FROM TB_MNG_GROUP WHERE TENANT_ID = #tenantId# AND GROUP_ID = A.GROUP_ID) AS DEPTH , (SELECT GRANT_NAME FROM TB_MNG_AUTH WHERE TENANT_ID = #tenantId# AND GRANT_ID = A.GRANT_ID) AS GRANT_NAME, B.AUTH_RANK , A.FORCED_LOGOUT, ISNULL(A.CONTROL_AUTH,'') CONTROL_AUTH, ISNULL(A.MAIN_PAGE,'') MAIN_PAGE , ISNULL(A.LOGIN_DATE_CHECK_USE,'') LOGIN_DATE_CHECK_USE , ISNULL(A.LOGIN_DATE_CHECK_FROM,'') LOGIN_DATE_CHECK_FROM , ISNULL(A.LOGIN_DATE_CHECK_TO,'') LOGIN_DATE_CHECK_TO , ISNULL(A.LOGIN_ERR_COUNT,0) LOGIN_ERR_COUNT , ISNULL((SELECT VAL_CUR FROM TB_WAS_CONFIG WHERE SECTION = 'COMPANY' AND KEY_CODE = 'SITE_PWD_FAIL_COUNT'), 0) SITE_PWD_FAIL_COUNT FROM TB_MNG_USERINFO A LEFT OUTER JOIN TB_MNG_AUTH B ON A.GRANT_ID = B.GRANT_ID AND A.TENANT_ID = B.TENANT_ID LEFT OUTER JOIN TB_MNG_GROUP C ON A.GROUP_ID = C.GROUP_ID AND A.TENANT_ID= C.TENANT_ID WHERE A.TENANT_ID = #tenantId# AND A.USER_ID = #agentId# AND A.RETIREE_FLAG != 0
A.USER_PWD = #agentPw#

/* ID : ARGOCOMMON.getIntroExtInfo */
select dn_status, count(*) cnt
	from (
		select case isnull(dn_status,'') when '01' then dn_status when '10' then dn_status else '00' end dn_status
			from tb_mng_usertelno
		where tenant_id = #tenantId#
		) t
group by dn_status
