sqlMap.select:
/* ID : bkLabel.getBkLabelList */ select BK_LABEL_ID, BK_DEVICE, MEDIA_KIND , DATE_FORMAT(STR_TO_DATE(FROM_CONDITION, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s') AS FROM_CONDITION , DATE_FORMAT(STR_TO_DATE(END_CONDITION, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s') AS END_CONDITION , STORE_PLACE, CAST(BK_FILE_CNT AS CHAR(10)) AS BK_FILE_CNT , CAST(USED_SPACE AS CHAR(10)) AS USED_SPACE, CAST(USABLE_SPACE AS CHAR(10)) AS USABLE_SPACE , RTRIM(CAST(STORE_YEAR AS CHAR(10))) AS STORE_YEAR , LAST_WORKER, DATE_FORMAT(LAST_BK_TIME, '%Y-%m-%d %H:%i:%s') AS LAST_BK_TIME, BK_RUN_FLAG, USE_RATE FROM ( SELECT * FROM (
SELECT TB_IN.* FROM (
SELECT BK_LABEL_ID, BK_DEVICE, MEDIA_KIND, FROM_CONDITION, END_CONDITION , STORE_PLACE, IFNULL(BK_FILE_CNT, 0) AS BK_FILE_CNT, IFNULL(USED_SPACE, 0) AS USED_SPACE , IFNULL(USABLE_SPACE, 0) AS USABLE_SPACE, IFNULL(STORE_YEAR, 0) AS STORE_YEAR , LAST_WORKER, LAST_BK_TIME , CASE BK_RUN_FLAG WHEN '1' THEN '진행' WHEN '2' THEN '완료' ELSE '' END AS BK_RUN_FLAG , ROUND((IFNULL(USED_SPACE, 0) * 100) / (IFNULL(USED_SPACE, 0) + IFNULL(USABLE_SPACE, 0))) AS USE_RATE FROM TB_SYS_BKLABEL
BK_LABEL_ID BK_DEVICE LAST_WORKER LIKE CONCAT('%', #findText#, '%')
FROM_CONDITION >= #findFromCondition#
END_CONDITION <= #findEndCondition#
) TB_IN
) TB_OUT
) tbl2 ORDER BY LAST_BK_TIME DESC

sqlMap.delete:
/* ID : bkLabel.setBkLabelDelete */
DELETE FROM TB_SYS_BKLABEL
 WHERE BK_LABEL_ID = #bkLabelId#
