sqlMap.select:
/* ID : comboBoxCode.getGroupList */ SELECT GROUP_ID AS CODE, REPLACE( LPAD('-|',LEVEL * 2, '-|' ) || GROUP_NAME, '-|', '&nbsp;&nbsp;' ) AS CODE_NM FROM TB_MNG_GROUP START WITH ( tenant_id = #findTenantId#
AND PARENT_ID = '0'
AND ( GROUP_ID = ( SELECT GROUP_ID FROM TB_MNG_USERINFO WHERE USER_ID = #userId# and TENANT_ID = #findTenantId# )
OR GROUP_ID in (select g.GROUP_ID from tb_mng_group g, tb_mng_userinfo u where g.tenant_id = u.tenant_id /*and instr(','||g.depth||',',','||control_auth||',') > 0*/ and instr(',' || control_auth || ',', ',' || g.GROUP_ID || ',') > 0 and u.user_id = #userId# and u.tenant_id = #findTenantId#)
)
AND ( GROUP_ID = ( SELECT GROUP_ID FROM TB_MNG_USERINFO WHERE USER_ID = #userId# and TENANT_ID = #findTenantId# )
OR GROUP_ID in (select g.GROUP_ID from tb_mng_group g, tb_mng_userinfo u where g.tenant_id = u.tenant_id /*and instr(','||g.depth||',',','||control_auth||',') > 0*/ and instr(',' || control_auth || ',', ',' || g.GROUP_ID || ',') > 0 and u.user_id = #userId# and u.tenant_id = #findTenantId#)
)
) CONNECT BY PARENT_ID = PRIOR GROUP_ID AND TENANT_ID = #findTenantId# ORDER SIBLINGS BY GROUP_ID

/* ID : comboBoxCode.getUserList */ select user_id code, user_name || '-' || user_id code_nm FROM tb_mng_userinfo where tenant_id = #findTenantId#
and group_id = lpad(#findGroupId#, instr(#findGroupId#, '_') - 1)
order by user_name

/* ID : comboBoxCode.getUserList_Parentheses */ select user_id code, user_name || '(' || user_id || ')' code_nm FROM tb_mng_userinfo where tenant_id = #findTenantId#
and group_id = lpad(#findGroupId#, instr(#findGroupId#, '_') - 1)
order by user_name
