sqlMap.select:
/* ID : bkLabel.getBkLabelList */ select bk_label_id, bk_device, media_kind , to_char(to_date(from_condition, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') as from_condition , to_char(to_date(end_condition, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') as end_condition , store_place, CAST(bk_file_cnt AS CHAR(10)) as bk_file_cnt , CAST(used_space AS CHAR(10)) as used_space, CAST(usable_space AS CHAR(10)) as usable_space , RTRIM(CAST(store_year AS CHAR(10))) as store_year , last_worker, to_char(last_bk_time, 'YYYY-MM-DD HH24:MI:SS') as last_bk_time, bk_run_flag, use_rate from( select * from ( select Row_Number() over (order by last_bk_time ) RowNum2, TB_IN.* from ( select Row_Number() over (order by last_bk_time desc) RowNum1 , bk_label_id, bk_device, media_kind, from_condition, end_condition , store_place, nvl(bk_file_cnt,0) bk_file_cnt, nvl(used_space,0) used_space , nvl(usable_space,0) usable_space, nvl(store_year,0) store_year , last_worker, last_bk_time, case bk_run_flag when '1' then '진행' when '2' then '완료' else '' end as bk_run_flag , round((nvl(used_space,0)*100) / (nvl(used_space,0)+nvl(usable_space,0))) as use_rate FROM tb_sys_bklabel
bk_label_id bk_device last_worker Like '%' || #findText# || '%'
from_condition >= #findFromCondition#
end_condition <= #findEndCondition#
) TB_IN
) TB_OUT
) tbl2 order by last_bk_time desc

sqlMap.delete:
/* ID : bkLabel.setBkLabelDelete */
delete from tb_sys_bklabel
where bk_label_id = #bkLabelId#
